const login = async (req, res, next) => {
    const correctCondition = Joi.object({
        email: Joi.string().required().email().trim().strict(),
        password: Joi.string().required().trim().strict(),
    });
    try {
        await correctCondition.validateAsync(req.body, {
            abortEarly: false,
        });
