import { Server } from 'socket.io';
import jwt from 'jsonwebtoken';

const adminSockets = new Map(); // Use a Map to store admin sockets
const userSockets = new Map(); // Use a Map to store user sockets
let ioInstance;

const setupSocketIo = (server) => {
    const io = new Server(server, {
        cors: {
            origin: '*', // Allow all origins; you can restrict it to your client URL
            methods: ['GET', 'POST'],
            allowedHeaders: ['Authorization'],
            credentials: true,
        },
    });

    io.use((socket, next) => {
        const token = socket.handshake.query.token;
        if (!token) {
            return next(new Error('Authentication error'));
        }

        jwt.verify(token, process.env.ACCESS_TOKEN_SECRET, (err, decoded) => {
            if (err) {
                return next(new Error('Authentication error'));
            }

            // Trích xuất thông tin từ token
            const userId = decoded.userId;
            const isAdmin = decoded.isAdmin;

            socket.userId = userId;
            socket.isAdmin = isAdmin;

            // Quản lý socket của admin và người dùng
            if (isAdmin) {
                if (!adminSockets.has(userId)) {
                    adminSockets.set(userId, new Set());
                }
                adminSockets.get(userId).add(socket.id);
            } else {
                if (!userSockets.has(userId)) {
                    userSockets.set(userId, new Set());
                }
                userSockets.get(userId).add(socket.id);
            }

            next();
        });
    });

    io.on('connection', (socket) => {
        console.log('New client connected', socket.id);

        socket.on('disconnect', () => {
            console.log('Client disconnected', socket.id);
            if (socket.isAdmin) {
                const adminSocketSet = adminSockets.get(socket.userId);
                if (adminSocketSet) {
                    adminSocketSet.delete(socket.id);
                    if (adminSocketSet.size === 0) {
                        adminSockets.delete(socket.userId);
                    }
                }
            } else {
                const userSocketSet = userSockets.get(socket.userId);
                if (userSocketSet) {
                    userSocketSet.delete(socket.id);
                    if (userSocketSet.size === 0) {
                        userSockets.delete(socket.userId);
                    }
                }
            }
        });
    });

    ioInstance = io;

    return io;
};

const sendMessageToAdmins = (message) => {
    if (!ioInstance) {
        throw new Error('Socket.io is not initialized');
    }

    adminSockets.forEach((sockets) => {
        sockets.forEach((socketId) => {
            ioInstance.to(socketId).emit('adminNotification', message);
        });
    });
};

const sendMessage = (message) => {
    if (!ioInstance) {
        throw new Error('Socket.io is not initialized');
    }

    // Send message to all users
    userSockets.forEach((sockets) => {
        sockets.forEach((socketId) => {
            ioInstance.to(socketId).emit('userNotification', message);
