        const errorMessage = error.details.map((detail) => detail.message).join(', ');
        const customError = new ApiError(StatusCodes.UNPROCESSABLE_ENTITY, errorMessage);
        next(customError);
    }
};
export const adminValidate = {
    benefitPlan,
    employeeValidate,
};

